<?phpuse Illuminate\Database\Capsule\Manager as DB;class AttributeController extends CommonController{    public function init() {        $this->table = 'attribute';        $this->primaryKey = 'id';        parent::init();    }	#产品属性添加	public function increaseAction() {		$name	=	$this->get('name','');		$inputs		= array(				['name'=>'name','value'=>$name,'role'=>'required|unique:attribute.name','msg'=>'产品属性名称'],		);		$result		= Validate::check($inputs);		if(	!empty($result) ){ret(1, $result);}        $values =   $this->get('values', []);        if(!empty($values)){            $values = array_unique($values);        }        $this->formData['values'] = implode(';', $values);		parent::increaseAction();    }	#产品属性修改	public function updateAction() {		$name	=	$this->get('name','');		$inputs	= array(				['name'=>'name','value'=>$name,'role'=>'required','msg'=>'产品属性名称'],		);		$result	= Validate::check($inputs);		if(	!empty($result) ){ret(1, $result);}        $values =   $this->get('values', []);        if(!empty($values)){            $values = array_unique($values);        }        $this->formData['values'] = implode(';', $values);        parent::updateAction();    }}