<?phpuse Illuminate\Database\Capsule\Manager as DB;class CommentController extends CommonController{    public function init() {        $this->table = 'comment';        $this->primaryKey = 'id';        parent::init();    }		public function getAction(){		$data = parent::getAction(TRUE);		if(!empty($data['rows'])){		foreach($data['rows'] as $k=>&$v){			$v['goods'] = DB::table('goods')->find($v['goods_id']);		}}		json($data);	}    public function editAction(){        $dataset = parent::editAction(TRUE);        $photos  = empty($dataset['photos']) ? [] : explode(',', $dataset['photos']);        $this->_view->assign('photos', $photos);        $goods     =DB::table('goods')->find($dataset['goods_id']);        $this->_view->assign('goods', $goods);    }	}