<?phpuse Illuminate\Database\Capsule\Manager as DB;class FeedbackController extends CommonController{    public function init() {        $this->table = 'notes';        $this->primaryKey = 'id';        parent::init();    }		public function getAction(){		$data = parent::getAction(TRUE);		if(!empty($data['rows'])){		foreach($data['rows'] as $k=>&$v){			$v['members'] = (new membersModel)->find($v['members_id']);		}}		json($data);	}    public function editAction(){        $dataset = parent::editAction(TRUE);        $photos  = empty($dataset['images']) ? [] : explode(',', $dataset['images']);        $this->_view->assign('photos', $photos);        $goods     =DB::table('goods')->find($dataset['goods_id']);        $this->_view->assign('goods', $goods);    }	}