<?phpuse Illuminate\Database\Capsule\Manager as DB;class LabelController extends CommonController{    public function init() {        $this->table = 'label';        $this->primaryKey = 'id';        parent::init();    }    #标签添加    public function increaseAction() {        $name	=	$this->get('name','');        $inputs		= array(            ['name'=>'name','value'=>$name,'role'=>'required|unique:label.name','msg'=>'产品标签'],        );        $result		= Validate::check($inputs);        if(	!empty($result) ){ret(1, $result);}        $images =   $this->get('images', []);        if(!empty($images)){            $this->formData['logo'] = $images[0];        }        unset($this->formData['images']);        parent::increaseAction();    }    #标签修改    public function updateAction() {        $name	=	$this->get('name','');        $inputs	= array(            ['name'=>'name','value'=>$name,'role'=>'required','msg'=>'产品标签有误'],        );        $result	= Validate::check($inputs);        if(	!empty($result) ){ret(1, $result);}        $images =   $this->get('images', []);        if(!empty($images)){            $this->formData['logo'] = $images[0];        }        unset($this->formData['images']);        parent::updateAction();    }}